/**
 * Accessibility Component Library - Reusable a11y patterns
 */

import React, { useState, useEffect, useRef, createContext, useContext, ReactNode } from 'react';

// Skip Links Component
export const SkipLinks: React.FC = () => {
  return (
    <nav className="skip-links" aria-label="Skip navigation">
      <a href="#main-content" className="skip-link">
        Skip to main content
      </a>
      <a href="#main-navigation" className="skip-link">
        Skip to navigation
      </a>
      <a href="#search" className="skip-link">
        Skip to search
      </a>
    </nav>
  );
};

// Live Region Component
interface LiveRegionProps {
  children: ReactNode;
  ariaLive?: 'polite' | 'assertive';
  id?: string;
}

export const LiveRegion: React.FC<LiveRegionProps> = ({ 
  children, 
  ariaLive = 'polite', 
  id 
}) => {
  return (
    <div
      id={id}
      role="status"
      aria-live={ariaLive}
      aria-atomic="true"
      className="sr-only"
    >
      {children}
    </div>
  );
};

// Focus Trap Component
interface FocusTrapProps {
  children: ReactNode;
  isActive?: boolean;
  returnFocus?: boolean;
}

export const FocusTrap: React.FC<FocusTrapProps> = ({ 
  children, 
  isActive = true,
  returnFocus = true 
}) => {
  const wrapperRef = useRef<HTMLDivElement>(null);
  const previousActiveElement = useRef<HTMLElement | null>(null);

  useEffect(() => {
    if (!isActive) return;

    previousActiveElement.current = document.activeElement as HTMLElement;
    const focusableElements = getFocusableElements(wrapperRef.current);

    if (focusableElements.length > 0) {
      focusableElements[0].focus();
    }

    const handleKeyDown = (e: KeyboardEvent) => {
      if (e.key !== 'Tab') return;

      const focusableElements = getFocusableElements(wrapperRef.current);
      if (focusableElements.length === 0) return;

      const firstElement = focusableElements[0];
      const lastElement = focusableElements[focusableElements.length - 1];

      if (e.shiftKey && document.activeElement === firstElement) {
        lastElement.focus();
        e.preventDefault();
      } else if (!e.shiftKey && document.activeElement === lastElement) {
        firstElement.focus();
        e.preventDefault();
      }
    };

    document.addEventListener('keydown', handleKeyDown);

    return () => {
      document.removeEventListener('keydown', handleKeyDown);
      if (returnFocus && previousActiveElement.current) {
        previousActiveElement.current.focus();
      }
    };
  }, [isActive, returnFocus]);

  return <div ref={wrapperRef} tabIndex={-1}>{children}</div>;
};

// Helper function to get focusable elements
function getFocusableElements(container: HTMLElement | null): HTMLElement[] {
  if (!container) return [];

  return Array.from(
    container.querySelectorAll(
      'button, [href], input, select, textarea, [tabindex]:not([tabindex="-1"])'
    )
  ).filter(el => {
    const isVisible = (el as HTMLElement).offsetWidth > 0 || (el as HTMLElement).offsetHeight > 0;
    const isEnabled = !(el as HTMLButtonElement).disabled;
    return isVisible && isEnabled;
  }) as HTMLElement[];
}

// Announcer Component
interface AnnouncerProps {
  children: ReactNode;
  priority?: 'polite' | 'assertive';
}

export const Announcer: React.FC<AnnouncerProps> = ({ children, priority = 'polite' }) => {
  const [announcement, setAnnouncement] = useState<string | null>(null);
  const [id] = useState(() => `announcer-${Math.random().toString(36).substr(2, 9)}`);

  useEffect(() => {
    if (children) {
      setAnnouncement(children as string);

      // Clear announcement after a delay
      const timer = setTimeout(() => {
        setAnnouncement(null);
      }, 1000);

      return () => clearTimeout(timer);
    }
  }, [children]);

  return (
    <div
      id={id}
      role="alert"
      aria-live={priority}
      aria-atomic="true"
      className="sr-only"
    >
      {announcement}
    </div>
  );
};

// Accessibility Context
interface AccessibilityContextType {
  reducedMotion: boolean;
  highContrast: boolean;
  fontSize: 'normal' | 'large' | 'larger';
  toggleReducedMotion: () => void;
  toggleHighContrast: () => void;
  setFontSize: (size: 'normal' | 'large' | 'larger') => void;
}

const AccessibilityContext = createContext<AccessibilityContextType | undefined>(undefined);

export const AccessibilityProvider: React.FC<{ children: ReactNode }> = ({ children }) => {
  const [reducedMotion, setReducedMotion] = useState(false);
  const [highContrast, setHighContrast] = useState(false);
  const [fontSize, setFontSize] = useState<'normal' | 'large' | 'larger'>('normal');

  useEffect(() => {
    // Check for user preference for reduced motion
    const mediaQuery = window.matchMedia('(prefers-reduced-motion: reduce)');
    setReducedMotion(mediaQuery.matches);

    const handleChange = (e: MediaQueryListEvent) => {
      setReducedMotion(e.matches);
    };

    mediaQuery.addEventListener('change', handleChange);
    return () => mediaQuery.removeEventListener('change', handleChange);
  }, []);

  const toggleReducedMotion = () => {
    setReducedMotion(prev => !prev);
  };

  const toggleHighContrast = () => {
    setHighContrast(prev => !prev);
  };

  const value: AccessibilityContextType = {
    reducedMotion,
    highContrast,
    fontSize,
    toggleReducedMotion,
    toggleHighContrast,
    setFontSize,
  };

  return (
    <AccessibilityContext.Provider value={value}>
      <div 
        className={`
          ${highContrast ? 'high-contrast' : ''}
          ${fontSize === 'large' ? 'font-size-large' : ''}
          ${fontSize === 'larger' ? 'font-size-larger' : ''}
          ${reducedMotion ? 'reduced-motion' : ''}
        `}
      >
        {children}
      </div>
    </AccessibilityContext.Provider>
  );
};

export const useAccessibility = () => {
  const context = useContext(AccessibilityContext);
  if (!context) {
    throw new Error('useAccessibility must be used within AccessibilityProvider');
  }
  return context;
};

// Accessible Button Component
interface AccessibleButtonProps {
  children: ReactNode;
  onClick: () => void;
  variant?: 'primary' | 'secondary' | 'outline';
  disabled?: boolean;
  ariaLabel?: string;
  className?: string;
}

export const AccessibleButton: React.FC<AccessibleButtonProps> = ({
  children,
  onClick,
  variant = 'primary',
  disabled = false,
  ariaLabel,
  className = ''
}) => {
  const handleClick = () => {
    if (!disabled) {
      onClick();
    }
  };

  const handleKeyDown = (e: React.KeyboardEvent) => {
    if (e.key === 'Enter' || e.key === ' ') {
      e.preventDefault();
      if (!disabled) {
        onClick();
      }
    }
  };

  const variantClasses = {
    primary: 'bg-blue-600 hover:bg-blue-700 text-white',
    secondary: 'bg-gray-600 hover:bg-gray-700 text-white',
    outline: 'border border-gray-600 hover:bg-gray-100 dark:hover:bg-gray-700 text-gray-700 dark:text-gray-200'
  };

  const disabledClasses = disabled 
    ? 'opacity-50 cursor-not-allowed' 
    : 'cursor-pointer';

  return (
    <button
      onClick={handleClick}
      onKeyDown={handleKeyDown}
      disabled={disabled}
      aria-label={ariaLabel}
      aria-disabled={disabled}
      tabIndex={disabled ? -1 : 0}
      className={`
        px-4 py-2 rounded-md font-medium transition-colors
        ${variantClasses[variant]} 
        ${disabledClasses} 
        ${className}
      `}
    >
      {children}
    </button>
  );
};

// Accessible Modal Component
interface AccessibleModalProps {
  isOpen: boolean;
  onClose: () => void;
  title: string;
  children: ReactNode;
  ariaLabel?: string;
}

export const AccessibleModal: React.FC<AccessibleModalProps> = ({
  isOpen,
  onClose,
  title,
  children,
  ariaLabel
}) => {
  const modalRef = useRef<HTMLDivElement>(null);

  useEffect(() => {
    if (isOpen && modalRef.current) {
      // Focus the modal when it opens
      modalRef.current.focus();
    }
  }, [isOpen]);

  useEffect(() => {
    if (isOpen) {
      // Prevent background scrolling
      document.body.style.overflow = 'hidden';

      // Handle escape key
      const handleEscape = (e: KeyboardEvent) => {
        if (e.key === 'Escape') {
          onClose();
        }
      };

      document.addEventListener('keydown', handleEscape);
      return () => {
        document.removeEventListener('keydown', handleEscape);
        document.body.style.overflow = '';
      };
    }
  }, [isOpen, onClose]);

  if (!isOpen) return null;

  return (
    <FocusTrap isActive={isOpen}>
      <div
        className="fixed inset-0 z-50 flex items-center justify-center p-4 bg-black bg-opacity-50"
        role="button"
        tabIndex={0}
        aria-label="Close modal"
        onKeyDown={(e) => { if (e.key === 'Enter' || e.key === ' ') { e.preventDefault(); onClose(); } }}
        onClick={onClose}
      >
        <div
          ref={modalRef}
          role="dialog"
          aria-modal="true"
          aria-labelledby={ariaLabel ? undefined : "modal-title"}
          aria-describedby="modal-description"
          tabIndex={-1}
          className="bg-white dark:bg-gray-800 rounded-lg shadow-xl w-full max-w-md"
          onClick={e => e.stopPropagation()}
        >
          <div className="p-6">
            <div className="flex justify-between items-center mb-4">
              <h2 
                id="modal-title"
                className="text-xl font-bold text-gray-900 dark:text-white"
              >
                {title}
              </h2>
              <button
                onClick={onClose}
                aria-label="Close modal"
                className="text-gray-500 hover:text-gray-700 dark:text-gray-400 dark:hover:text-gray-200"
              >
                <span className="sr-only">Close</span>
                <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M6 18L18 6M6 6l12 12" />
                </svg>
              </button>
            </div>
            <div id="modal-description">
              {children}
            </div>
          </div>
        </div>
      </div>
    </FocusTrap>
  );
};

// Accessible Table Component
interface AccessibleTableProps {
  headers: string[];
  data: any[][];
  caption?: string;
  className?: string;
}

export const AccessibleTable: React.FC<AccessibleTableProps> = ({
  headers,
  data,
  caption,
  className = ''
}) => {
  return (
    <div className="overflow-x-auto">
      <table className={`min-w-full divide-y divide-gray-200 dark:divide-gray-700 ${className}`}>
        {caption && (
          <caption className="sr-only">
            {caption}
          </caption>
        )}
        <thead className="bg-gray-50 dark:bg-gray-700">
          <tr>
            {headers.map((header, index) => (
              <th
                key={index}
                scope="col"
                className="px-6 py-3 text-left text-xs font-medium text-gray-500 dark:text-gray-300 uppercase tracking-wider"
              >
                {header}
              </th>
            ))}
          </tr>
        </thead>
        <tbody className="bg-white dark:bg-gray-800 divide-y divide-gray-200 dark:divide-gray-700">
          {data.map((row, rowIndex) => (
            <tr key={rowIndex} className={rowIndex % 2 === 0 ? 'bg-white dark:bg-gray-800' : 'bg-gray-50 dark:bg-gray-700'}>
              {row.map((cell, cellIndex) => (
                <td
                  key={cellIndex}
                  className="px-6 py-4 whitespace-nowrap text-sm text-gray-500 dark:text-gray-300"
                >
                  {cell}
                </td>
              ))}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

// Accessible Form Components
interface AccessibleInputProps {
  id: string;
  label: string;
  type?: string;
  value: string;
  onChange: (value: string) => void;
  error?: string;
  required?: boolean;
  className?: string;
}

export const AccessibleInput: React.FC<AccessibleInputProps> = ({
  id,
  label,
  type = 'text',
  value,
  onChange,
  error,
  required = false,
  className = ''
}) => {
  return (
    <div className="mb-4">
      <label
        htmlFor={id}
        className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-1"
      >
        {label} {required && <span className="text-red-500">*</span>}
      </label>
      <input
        id={id}
        type={type}
        value={value}
        onChange={(e) => onChange(e.target.value)}
        required={required}
        aria-invalid={!!error}
        aria-describedby={error ? `${id}-error` : undefined}
        className={`
          w-full px-3 py-2 border rounded-md shadow-sm
          focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500
          ${error ? 'border-red-500' : 'border-gray-300 dark:border-gray-600'}
          ${className}
        `}
      />
      {error && (
        <p id={`${id}-error`} className="mt-1 text-sm text-red-600">
          {error}
        </p>
      )}
    </div>
  );
};

// Accessible Select Component
interface AccessibleSelectProps {
  id: string;
  label: string;
  value: string;
  onChange: (value: string) => void;
  options: { value: string; label: string }[];
  error?: string;
  required?: boolean;
}

export const AccessibleSelect: React.FC<AccessibleSelectProps> = ({
  id,
  label,
  value,
  onChange,
  options,
  error,
  required = false
}) => {
  return (
    <div className="mb-4">
      <label
        htmlFor={id}
        className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-1"
      >
        {label} {required && <span className="text-red-500">*</span>}
      </label>
      <select
        id={id}
        value={value}
        onChange={(e) => onChange(e.target.value)}
        required={required}
        aria-invalid={!!error}
        aria-describedby={error ? `${id}-error` : undefined}
        className={`
          w-full px-3 py-2 border rounded-md shadow-sm
          focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500
          ${error ? 'border-red-500' : 'border-gray-300 dark:border-gray-600'}
        `}
      >
        {options.map(option => (
          <option key={option.value} value={option.value}>
            {option.label}
          </option>
        ))}
      </select>
      {error && (
        <p id={`${id}-error`} className="mt-1 text-sm text-red-600">
          {error}
        </p>
      )}
    </div>
  );
};

// Accessible Checkbox Component
interface AccessibleCheckboxProps {
  id: string;
  label: string;
  checked: boolean;
  onChange: (checked: boolean) => void;
  error?: string;
  required?: boolean;
}

export const AccessibleCheckbox: React.FC<AccessibleCheckboxProps> = ({
  id,
  label,
  checked,
  onChange,
  error,
  required = false
}) => {
  return (
    <div className="flex items-start mb-4">
      <div className="flex items-center h-5">
        <input
          id={id}
          type="checkbox"
          checked={checked}
          onChange={(e) => onChange(e.target.checked)}
          required={required}
          aria-invalid={!!error}
          aria-describedby={error ? `${id}-error` : undefined}
          className="h-4 w-4 text-blue-600 focus:ring-blue-500 border-gray-300 rounded"
        />
      </div>
      <div className="ml-3 text-sm">
        <label
          htmlFor={id}
          className="font-medium text-gray-700 dark:text-gray-300"
        >
          {label} {required && <span className="text-red-500">*</span>}
        </label>
        {error && (
          <p id={`${id}-error`} className="text-red-600">
            {error}
          </p>
        )}
      </div>
    </div>
  );
};

import React, { useState } from 'react';
import { Card, CardContent } from '@/components/ui/card';
import { Checkbox } from '@/components/ui/checkbox';
import { Badge } from '@/components/ui/badge';
import { Button } from '@/components/ui/button';
import { 
  FileText, 
  CheckCircle, 
  XCircle, 
  FileImage,
  File,
  Database,
  Eye,
  Hash,
  ArrowUpDown,
  ArrowUp,
  ArrowDown,
  Download,
  MoreVertical,
  Clock,
  RefreshCw
} from 'lucide-react';
import { formatFileSize } from '@/lib/utils';
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuTrigger,
} from '@/components/ui/dropdown-menu';

interface BatchResult {
  id: string;
  filename: string;
  status: 'success' | 'error' | 'processing' | 'pending';
  extractionDate: string;
  fieldsExtracted: number;
  fileSize: number;
  fileType: string;
  authenticityScore?: number;
  metadata: Record<string, any>;
  processingTime?: number;
  errorMessage?: string;
  batchId?: string;
}

interface BatchGridProps {
  results: BatchResult[];
  selectedFiles: string[];
  onSelectFile: (id: string) => void;
  onSelectAll: () => void;
  allSelected: boolean;
  viewMode: 'grid' | 'list';
  sortBy?: 'filename' | 'date' | 'fields' | 'size' | 'status';
  sortOrder?: 'asc' | 'desc';
  onSortChange?: (field: 'filename' | 'date' | 'fields' | 'size' | 'status', order: 'asc' | 'desc') => void;
  onFileAction?: (action: 'download' | 'view' | 'reprocess', fileId: string) => void;
}

export const BatchGrid: React.FC<BatchGridProps> = ({
  results,
  selectedFiles,
  onSelectFile,
  onSelectAll,
  allSelected,
  viewMode,
  sortBy = 'date',
  sortOrder = 'desc',
  onSortChange,
  onFileAction
}) => {
  const [expandedCards, setExpandedCards] = useState<Set<string>>(new Set());

  const getStatusIcon = (status: BatchResult['status']) => {
    switch (status) {
      case 'success': return <CheckCircle className="w-4 h-4 text-emerald-500" />;
      case 'error': return <XCircle className="w-4 h-4 text-red-500" />;
      case 'processing': return <RefreshCw className="w-4 h-4 text-yellow-500 animate-spin" />;
      case 'pending': return <Clock className="w-4 h-4 text-blue-500" />;
      default: return null;
    }
  };

  const getStatusBadge = (status: BatchResult['status']) => {
    switch (status) {
      case 'success': 
        return <Badge variant="secondary" className="bg-emerald-500/20 text-emerald-400 border-emerald-500/30">Success</Badge>;
      case 'error': 
        return <Badge variant="secondary" className="bg-red-500/20 text-red-400 border-red-500/30">Error</Badge>;
      case 'processing': 
        return <Badge variant="secondary" className="bg-yellow-500/20 text-yellow-400 border-yellow-500/30 animate-pulse">Processing</Badge>;
      case 'pending': 
        return <Badge variant="secondary" className="bg-blue-500/20 text-blue-400 border-blue-500/30">Pending</Badge>;
      default: 
        return <Badge variant="outline">Unknown</Badge>;
    }
  };

  const getFileTypeIcon = (fileType: string) => {
    if (fileType.includes('image')) return <FileImage className="w-4 h-4 text-blue-500" />;
    if (fileType.includes('pdf')) return <FileText className="w-4 h-4 text-red-500" />;
    if (fileType.includes('dicom')) return <Database className="w-4 h-4 text-purple-500" />;
    return <File className="w-4 h-4 text-slate-500" />;
  };

  const toggleExpanded = (id: string) => {
    setExpandedCards((prev: Set<string>) => {
      const newSet = new Set(prev);
      if (newSet.has(id)) {
        newSet.delete(id);
      } else {
        newSet.add(id);
      }
      return newSet;
    });
  };

  const getSortIcon = (field: string) => {
    if (sortBy !== field) return <ArrowUpDown className="w-4 h-4" />;
    return sortOrder === 'asc' ? <ArrowUp className="w-4 h-4" /> : <ArrowDown className="w-4 h-4" />;
  };

  const handleSort = (field: 'filename' | 'date' | 'fields' | 'size' | 'status') => {
    if (!onSortChange) return;

    if (sortBy === field) {
      onSortChange(field, sortOrder === 'asc' ? 'desc' : 'asc');
    } else {
      onSortChange(field, 'asc');
    }
  };

  const renderGridView = () => (
    <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-4">
      {results.map((result) => {
        const isExpanded = expandedCards.has(result.id);

        return (
          <Card 
            key={result.id} 
            className={`bg-card border-white/10 hover:bg-muted/20 transition-all duration-200 ${
              selectedFiles.includes(result.id) ? 'ring-2 ring-primary/50 shadow-lg' : ''
            } ${result.status === 'processing' ? 'animate-pulse' : ''}`}
          >
            <CardContent className="p-4">
              <div className="flex items-start gap-3">
                <Checkbox
                  checked={selectedFiles.includes(result.id)}
                  onCheckedChange={() => onSelectFile(result.id)}
                  className="mt-1 data-[state=checked]:bg-primary data-[state=checked]:text-primary-foreground"
                />

                <div className="flex-1 min-w-0">
                  <div className="flex items-start justify-between gap-2 mb-3">
                    <div className="flex items-center gap-2 flex-1 min-w-0">
                      {getFileTypeIcon(result.fileType)}
                      <h3 className="font-medium text-white truncate" title={result.filename}>
                        {result.filename}
                      </h3>
                    </div>
                    <div className="flex items-center gap-1">
                      {getStatusIcon(result.status)}
                      <DropdownMenu>
                        <DropdownMenuTrigger asChild>
                          <Button variant="ghost" size="sm" className="h-6 w-6 p-0">
                            <MoreVertical className="w-3 h-3" />
                          </Button>
                        </DropdownMenuTrigger>
                        <DropdownMenuContent align="end">
                          <DropdownMenuItem onClick={() => onFileAction?.('view', result.id)}>
                            <Eye className="w-4 h-4 mr-2" />
                            View Details
                          </DropdownMenuItem>
                          {result.status === 'success' && (
                            <DropdownMenuItem onClick={() => onFileAction?.('download', result.id)}>
                              <Download className="w-4 h-4 mr-2" />
                              Download Metadata
                            </DropdownMenuItem>
                          )}
                          {result.status === 'error' && (
                            <DropdownMenuItem onClick={() => onFileAction?.('reprocess', result.id)}>
                              <RefreshCw className="w-4 h-4 mr-2" />
                              Reprocess File
                            </DropdownMenuItem>
                          )}
                        </DropdownMenuContent>
                      </DropdownMenu>
                    </div>
                  </div>

                  <div className="space-y-2">
                    <div className="flex items-center gap-2 text-xs text-slate-300">
                      <span>{result.fileType.split('/')[1]?.toUpperCase() || result.fileType}</span>
                      <span>•</span>
                      <span>{formatFileSize(result.fileSize)}</span>
                    </div>

                    <div className="flex items-center justify-between">
                      <div className="flex items-center gap-2">
                        <Hash className="w-3 h-3 text-slate-400" />
                        <span className="text-xs text-slate-300">Fields:</span>
                        <span className="text-sm font-medium text-white">{result.fieldsExtracted}</span>
                      </div>
                      {result.processingTime && (
                        <div className="flex items-center gap-1 text-xs text-slate-400">
                          <Clock className="w-3 h-3" />
                          {Math.round(result.processingTime / 1000)}s
                        </div>
                      )}
                    </div>

                    {result.authenticityScore !== undefined && (
                      <div className="flex items-center gap-2">
                        <Eye className="w-3 h-3 text-primary" />
                        <span className="text-xs text-slate-300">Authenticity:</span>
                        <Badge 
                          variant="secondary" 
                          className={`text-xs ${
                            result.authenticityScore >= 80 
                              ? 'bg-emerald-500/20 text-emerald-400' 
                              : result.authenticityScore >= 50 
                              ? 'bg-yellow-500/20 text-yellow-400'
                              : 'bg-red-500/20 text-red-400'
                          }`}
                        >
                          {result.authenticityScore}%
                        </Badge>
                      </div>
                    )}

                    {result.errorMessage && (
                      <div className="p-2 bg-red-500/10 border border-red-500/20 rounded text-xs text-red-400">
                        {result.errorMessage}
                      </div>
                    )}

                    <div className="text-xs text-slate-400">
                      {new Date(result.extractionDate).toLocaleString()}
                    </div>
                  </div>

                  {isExpanded && (
                    <div className="mt-3 pt-3 border-t border-white/10 space-y-2">
                      <div className="text-xs">
                        <span className="text-slate-400">File ID:</span>
                        <span className="text-white font-mono ml-2">{result.id.slice(0, 8)}...</span>
                      </div>
                      {Object.entries(result.metadata).slice(0, 3).map(([key, value]) => (
                        <div key={key} className="text-xs">
                          <span className="text-slate-400 capitalize">{key.replace(/_/g, ' ')}:</span>
                          <span className="text-white ml-2 truncate">{String(value)}</span>
                        </div>
                      ))}
                      {Object.keys(result.metadata).length > 3 && (
                        <div className="text-xs text-slate-400">
                          +{Object.keys(result.metadata).length - 3} more fields
                        </div>
                      )}
                    </div>
                  )}
                </div>
              </div>

              <div className="mt-3 pt-3 border-t border-white/10 flex items-center justify-between">
                {getStatusBadge(result.status)}
                <Button
                  variant="ghost"
                  size="sm"
                  onClick={() => toggleExpanded(result.id)}
                  className="h-6 text-xs text-slate-400 hover:text-white"
                >
                  {isExpanded ? 'Show Less' : 'Show More'}
                </Button>
              </div>
            </CardContent>
          </Card>
        );
      })}
    </div>
  );

  const renderListView = () => (
    <div className="space-y-2">
      {/* List Header */}
      <div className="grid grid-cols-12 gap-4 p-3 bg-muted/30 rounded-lg text-sm font-medium text-slate-300">
        <div className="col-span-1">
          <Checkbox
            checked={allSelected}
            onCheckedChange={onSelectAll}
            className="data-[state=checked]:bg-primary data-[state=checked]:text-primary-foreground"
          />
        </div>
        <button type="button" aria-label="Sort by filename" className="col-span-4 flex items-center gap-2 cursor-pointer hover:text-white text-left" onClick={() => handleSort('filename')}>
          File
          {getSortIcon('filename')}
        </button>
        <button type="button" aria-label="Sort by status" className="col-span-1 flex items-center gap-2 cursor-pointer hover:text-white text-left" onClick={() => handleSort('status')}>
          Status
          {getSortIcon('status')}
        </button>
        <button type="button" aria-label="Sort by fields" className="col-span-1 flex items-center gap-2 cursor-pointer hover:text-white text-left" onClick={() => handleSort('fields')}>
          Fields
          {getSortIcon('fields')}
        </button>
        <button type="button" aria-label="Sort by size" className="col-span-1 flex items-center gap-2 cursor-pointer hover:text-white text-left" onClick={() => handleSort('size')}>
          Size
          {getSortIcon('size')}
        </button>
        <button type="button" aria-label="Sort by date" className="col-span-2 flex items-center gap-2 cursor-pointer hover:text-white text-left" onClick={() => handleSort('date')}>
          Date
          {getSortIcon('date')}
        </button>
        <div className="col-span-2">Authenticity</div>
        <div className="col-span-1">Actions</div>
      </div>

      {/* List Items */}
      {results.map((result) => (
        <div
          key={result.id}
          className={`grid grid-cols-12 gap-4 p-3 bg-card border border-white/10 rounded-lg hover:bg-muted/20 transition-colors ${
            selectedFiles.includes(result.id) ? 'ring-2 ring-primary/50' : ''
          } ${result.status === 'processing' ? 'animate-pulse' : ''}`}
        >
          <div className="col-span-1 flex items-center">
            <Checkbox
              checked={selectedFiles.includes(result.id)}
              onCheckedChange={() => onSelectFile(result.id)}
              className="data-[state=checked]:bg-primary data-[state=checked]:text-primary-foreground"
            />
          </div>

          <div className="col-span-4 flex items-center gap-3 min-w-0">
            {getFileTypeIcon(result.fileType)}
            <div className="flex-1 min-w-0">
              <p className="text-white font-medium truncate" title={result.filename}>
                {result.filename}
              </p>
              <p className="text-xs text-slate-400">
                {result.fileType} • {new Date(result.extractionDate).toLocaleDateString()}
              </p>
            </div>
          </div>

          <div className="col-span-1 flex items-center">
            {getStatusIcon(result.status)}
          </div>

          <div className="col-span-1 flex items-center">
            <div className="flex items-center gap-1">
              <Hash className="w-3 h-3 text-slate-400" />
              <span className="text-white">{result.fieldsExtracted}</span>
            </div>
          </div>

          <div className="col-span-1 flex items-center text-slate-300">
            {formatFileSize(result.fileSize)}
          </div>

          <div className="col-span-2 flex items-center text-slate-300 text-sm">
            <Clock className="w-3 h-3 mr-1" />
            {new Date(result.extractionDate).toLocaleString()}
          </div>

          <div className="col-span-2 flex items-center">
            {result.authenticityScore !== undefined ? (
              <Badge 
                variant="secondary" 
                className={`text-xs ${
                  result.authenticityScore >= 80 
                    ? 'bg-emerald-500/20 text-emerald-400' 
                    : result.authenticityScore >= 50 
                    ? 'bg-yellow-500/20 text-yellow-400'
                    : 'bg-red-500/20 text-red-400'
                }`}
              >
                {result.authenticityScore}%
              </Badge>
            ) : (
              <span className="text-slate-400 text-sm">N/A</span>
            )}
          </div>

          <div className="col-span-1 flex items-center">
            <DropdownMenu>
              <DropdownMenuTrigger asChild>
                <Button variant="ghost" size="sm" className="h-6 w-6 p-0">
                  <MoreVertical className="w-3 h-3" />
                </Button>
              </DropdownMenuTrigger>
              <DropdownMenuContent align="end">
                <DropdownMenuItem onClick={() => onFileAction?.('view', result.id)}>
                  <Eye className="w-4 h-4 mr-2" />
                  View Details
                </DropdownMenuItem>
                {result.status === 'success' && (
                  <DropdownMenuItem onClick={() => onFileAction?.('download', result.id)}>
                    <Download className="w-4 h-4 mr-2" />
                    Download Metadata
                  </DropdownMenuItem>
                )}
                {result.status === 'error' && (
                  <DropdownMenuItem onClick={() => onFileAction?.('reprocess', result.id)}>
                    <RefreshCw className="w-4 h-4 mr-2" />
                    Reprocess File
                  </DropdownMenuItem>
                )}
              </DropdownMenuContent>
            </DropdownMenu>
          </div>
        </div>
      ))}
    </div>
  );

  return (
    <div className="space-y-4">
      {/* Controls */}
      <div className="flex items-center justify-between">
        <div className="flex items-center gap-3 p-3 bg-muted/30 rounded-lg flex-1">
          <Checkbox
            checked={allSelected}
            onCheckedChange={onSelectAll}
            className="data-[state=checked]:bg-primary data-[state=checked]:text-primary-foreground"
          />
          <span className="text-sm font-medium text-white">Select All</span>
          <span className="text-sm text-slate-300">
            ({selectedFiles.length} of {results.length} selected)
          </span>
          {results.length > 0 && (
            <Badge variant="secondary" className="ml-auto">
              {viewMode === 'grid' ? 'Grid View' : 'List View'}
            </Badge>
          )}
        </div>
      </div>

      {/* Results */}
      {viewMode === 'grid' ? renderGridView() : renderListView()}

      {results.length === 0 && (
        <div className="text-center py-12 text-slate-500">
          <FileText className="w-12 h-12 mx-auto mb-4 opacity-50" />
          <p className="text-white mb-2">No batch results to display</p>
          <p className="text-sm text-slate-300">
            {viewMode === 'grid' 
              ? 'Upload multiple files to see batch processing results in grid view'
              : 'Upload multiple files to see batch processing results in list view'
            }
          </p>
        </div>
      )}
    </div>
  );
};
